import { Layout, Menu, Breadcrumb, Icon } from 'antd';
import React, { Component } from 'react';
import 'antd/dist/antd.css';
import logo from '../logo.svg';
import './Navi.css';
import { Button } from 'antd';
import { Input } from 'antd';
import { Collapse } from 'antd';
import Web3 from "web3";

let web3 = window.web3;
const { Header, Content, Footer, Sider } = Layout;
const { TextArea } = Input;
const { Panel } = Collapse;
const text = `
  A dog is a type of domesticated animal.
  Known for its loyalty and faithfulness,
  it can be found as a welcome guest in many households across the world.
`;
const onChange = e => {
    console.log(e);
};
const CONTRACT = "0xd4b7ed93256Df5E6FeA90977C835850Ac5F17873";

var notes = [
    // {content : '' , idx: "123"}
    // { username: 'Jerry', age: 21, id: "1", gender: 'male' },
    // { username: 'Tomy', age: 22, id: "2", gender: 'male' },
    // { username: 'Lily', age: 19, id: "3", gender: 'female' },
    // { username: 'Lucy', age: 20, id: "4", gender: 'female' }
]


class SiderDemo extends Component {
    state = {
        collapsed: false,
        mode: 'inline',
        loading: false,
        iconLoading: false,
        showstate: false,
        length: 10,
        idx:0,
        content: "" 
    };

    componentWillMount() {
        window.addEventListener("load", async () => {
        //   if (window.ethereum) {
        //     // console.log("init metamask", window.ethereum);
        //     web3 = new Web3(window.ethereum);
        //     await window.ethereum.enable();
        //     web3.eth.getAccounts(function(err, accounts) {
        //       web3.eth.defaultAccount = accounts[0];
        //       alert(web3.eth.defaultAccount);
        //     });
        //   } else {
            console.log("inject sdk");
            web3 = new Web3(
              new Web3.providers.HttpProvider("http://localhost:7545")
            );
            // 添加Ganache第一个账户的私钥
            web3.eth.accounts.wallet.add(
              "0xcf3fd2ee4a0fffb930a88df1d594ec9f88fafd256fc400665c4aa4209d0b6870"
            );

            web3.eth.defaultAccount = "0x3414d7268DDD6EC483f6EdB78F1369F6732C5D45";
        //   }

          this.getlength();
        // this.WriteNote();
            this.Read();
        });

      }

    getlength = async () => {
        // alert(web3.eth.defaultAccount);
        // alert("hello2");
        if( web3.eth.defaultAccount != 0){
            const payload = web3.eth.abi.encodeFunctionCall(
                {
                  name: "Length",
                  type: "function",
                  inputs: [{
                      type: 'address',
                      name: '_address'
                  }]
                },
                [web3.eth.defaultAccount]
            );

            const res = await web3.eth.call({
                to: CONTRACT,
                data: payload
            });

            this.setState({
                length: web3.eth.abi.decodeParameter("uint256", res)
            });  
            // alert(this.state.length);
        }

    };

    WriteNote = async () => {
        const funcSig = web3.eth.abi.encodeFunctionSignature("Write(address,string)");
        const param = web3.eth.abi.encodeParameters(['address','string'],[web3.eth.defaultAccount,'HiHiHi!']);
        try {
            await web3.eth.sendTransaction({
              to: CONTRACT, // contract address
              data: funcSig + param.slice(2),
              gas: 2000000
            });
            // alert("write success");
          } catch (e) {
            // alert(e.message);
          } finally {

          }

    };

    Read = async () => {
        var i=0;
        if( web3.eth.defaultAccount != 0){
            for(i=this.state.idx;i<this.state.length;i++){
                const payload = web3.eth.abi.encodeFunctionCall(
                    {
                      name: "Read",
                      type: "function",
                      inputs: [{
                          type: 'address',
                          name: '_address',
                      },{
                          type: 'uint256',
                          name: 'index',
                      }]
                    },
                    [web3.eth.defaultAccount,i]
                );
                const res = await web3.eth.call({
                    to: CONTRACT,
                    data: payload
                });
                this.setState({
                    content: web3.eth.abi.decodeParameter("string", res),
                    idx:i+1
                });   
                notes.push({'content': this.state.content,'idx': i.toString()})
                this.changeShowState2true();
            }          


        }

    };

    toggle = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    }

    enterLoading = () => {
        this.setState({ loading: true });
    };

    enterIconLoading = () => {
        this.setState({ iconLoading: true });
    };

    changeShowState2true() {
        this.setState((state) => {
          // 重要：在更新的时候读取 `state`，而不是 `this.state`。
          return {showstate: true}
        });
      }

    click_1 = (data) => {

        // alert(this.state.showstate);
        document.getElementById("_button").innerHTML="";
        document.getElementById("_input").innerHTML="";
        // notes.push({ username: 'Jerry', age: 21, id: '1', gender: 'male' })
        // alert(`${data}`);
    };
    click_2 = () => {
        document.getElementById("_button").innerHTML="\
        \<button type =\"button\" class=\"ant-btn ant-btn-primary\" id = \"_button\" onClick=alert(\"1234\")>\
        \<span>Click to Upload!</span>\
        \</button>";
        document.getElementById("_input").innerHTML="\
        \<div id=\"_input\" separator=\"/\">\
        \    <span class=\"ant-input-affix-wrapper\">\
        \        <input placeholder=\"fuck2\" type=\"text\" class=\"ant-input\" value></input>\
        \        <span class=\"ant-input-suffix\"></span>\
        \   </span>\
        \   <br></br>\
        \   <span class=\"ant-input-affix-wrapper ant-input-affix-wrapper-textarea-with-clear-btn\">\
        \       <textarea rows=\"25\" cols=\"20\" placeholder=\"fuck2\" class=\"ant-input\"></textarea>\
        \   </span>\
        \</div>\
        ";
    };
    click_3 = () => {
        this.WriteNote();
        this.getlength(); 
        this.Read();
        document.getElementById("_button").innerHTML="\
        \<button type =\"button\" class=\"ant-btn ant-btn-primary\" id = \"_button\" onClick=alert(\"1234\")>\
        \<span>Click to Find!</span>\
        \</button>";
        document.getElementById("_input").innerHTML="\
        \<div id=\"_input\" separator=\"/\">\
        \    <span class=\"ant-input-affix-wrapper\">\
        \        <input placeholder=\"fuck3\" type=\"text\" class=\"ant-input\" value></input>\
        \        <span class=\"ant-input-suffix\"></span>\
        \   </span>\
        \   <br></br>\
        \   <span class=\"ant-input-affix-wrapper ant-input-affix-wrapper-textarea-with-clear-btn\">\
        \       <textarea rows=\"25\" cols=\"20\" placeholder=\"fuck3\" class=\"ant-input\"></textarea>\
        \   </span>\
        \</div>\
        ";
    };
    // clicking My notes triggers
    render() {
        const { name } = this.state;
        return (
            <Layout>
                <Sider
                    trigger={null}
                    collapsible
                    collapsed={this.state.collapsed}
                >

                    <div className="logo" >
                        <span className="nav-text"  ></span>
                    </div>
                    <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                        <Menu.Item key="1">
                            <Icon type="user" />
                            <span className="nav-text" onClick={() => {this.click_1("hello")}}>My Notes</span>
                        </Menu.Item>
                        <Menu.Item key="2">
                            <Icon type="upload" />
                            <span className="nav-text" onClick={() => {this.click_2()}}>Upload new notes</span>
                        </Menu.Item>
                        <Menu.Item key="3">
                            <Icon type="right-circle" />
                            <span className="nav-text" onClick={() => {this.click_3()}}>Find notes</span>
                        </Menu.Item>
                    </Menu>
                </Sider>
                <Layout>
                    <Header style={{ background: '#000', padding: 0 }}>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>
                            <Icon
                                className="trigger"
                                type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
                                onClick={this.toggle}
                                style={{ cursor: 'pointer' }}
                            />
                        </span>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>Dapp Note</span>
                        <span style={{ color: '#fff', float: 'right', paddingRight: '1%' }}>
                            <img src={logo} className="App-logo" alt="logo" />
                        </span>
                        {/* <div onClick={this.click_1(name)}> test react </div> */}
                    </Header>

                    <Content style={{ margin: '0 160px' }} >
                        <Breadcrumb style={{ margin: '20px 0' }}>
                            <Breadcrumb.Item>Here is</Breadcrumb.Item>
                            <div id="shownotes">
                            <Collapse accordion>
                                    {notes.map((note) => {
                                    return (
                                        <Panel header={note.idx} key={note.idx}>
                                            <p>{note.content}</p>
                                        </Panel>
                                    )
                                    })}
    {/* 
                                    <Panel header="This is panel header 1" key="1">
                                    <p>{text}</p>
                                    </Panel>
                                    <Panel header="This is panel header 2" key="2">
                                    <p>{text}</p>
                                    </Panel>
                                    <Panel header="This is panel header 3" key="3">
                                    <p>{text}</p>
                                    </Panel> */}
                                </Collapse>
                            </div>

                            {/* <div>
                                {users.map((user) => {
                                return (
                                    <div>
                                    <div>姓名：{user.username}</div>
                                    <div>年龄：{user.age}</div>
                                    <div>性别：{user.gender}</div>
                                    <hr />
                                    </div>
                                )
                                })}
                            </div> */}
                            <div id = "_input">
                                {/* <Input  placeholder="input with clear icon" allowClear onChange={onChange} />
                                <br />
                                <br />
                                <TextArea rows="25" cols="20" placeholder="textarea with clear icon" allowClear onChange={onChange} /> */}
