import { Layout, Menu, Breadcrumb, Icon } from 'antd';
import React, { Component } from 'react';
import 'antd/dist/antd.css';
import logo from '../logo.svg';
import './Navi.css';
import { Button } from 'antd';
import { Input } from 'antd';
import { Collapse } from 'antd';
import Web3 from "web3";



let web3 = window.web3;
const { Header, Content, Footer, Sider } = Layout;
const { TextArea,Search } = Input;
const { Panel } = Collapse;
const text = `
  A dog is a type of domesticated animal.
  Known for its loyalty and faithfulness,
  it can be found as a welcome guest in many households across the world.
`;
const onChange = e => {
    console.log(e);
};
const CONTRACT = "0xd4b7ed93256Df5E6FeA90977C835850Ac5F17873";

var notes = [
    // {content : '' , idx: "123"}
    // { username: 'Jerry', age: 21, id: "1", gender: 'male' },
    // { username: 'Tomy', age: 22, id: "2", gender: 'male' },
    // { username: 'Lily', age: 19, id: "3", gender: 'female' },
    // { username: 'Lucy', age: 20, id: "4", gender: 'female' }
]


class SiderDemo extends Component {
    state = {
        collapsed: false,
        mode: 'inline',
        loading: false,
        iconLoading: false,
        showstate: false,
        length: 10,
        idx:0,
        content: "",
        mynotes:"" ,
        loading:false,
        account:"",
        componnetArray:[1],
        deskDivWidth:800,
        deskHeight:document.body.clientHeight
    };

    autodivheight = () => {
        var winHeight=0;
    if (window.innerHeight)
        winHeight = window.innerHeight;
    else if ((document.body) && (document.body.clientHeight))
        winHeight = document.body.clientHeight;
    //通过深入Document内部对body进行检测，获取浏览器窗口高度
    if (document.documentElement && document.documentElement.clientHeight)
        winHeight = document.documentElement.clientHeight;
    //DIV高度为浏览器窗口的高度
    document.getElementById("test").style.height= winHeight +"px";
    //DIV高度为浏览器窗口高度的一半
    document.getElementById("test").style.height= winHeight/2 +"px";
    }

    addComponent = () => {
        let arr = [...this.state.componnetArray];
        arr.push(this.state.componnetArray.length+1);
        this.setState({
            componnetArray: arr
        })
        alert(this.state.componnetArray);
    }

    componentWillMount() {
        window.addEventListener("load", async () => {
        //   if (window.ethereum) {
        //     // console.log("init metamask", window.ethereum);
        //     web3 = new Web3(window.ethereum);
        //     await window.ethereum.enable();
        //     web3.eth.getAccounts(function(err, accounts) {
        //       web3.eth.defaultAccount = accounts[0];
        //       alert(web3.eth.defaultAccount);
        //     });
        //   } else {
            console.log("inject sdk");
            web3 = new Web3(
              new Web3.providers.HttpProvider("http://localhost:7545")
            );
            // 添加Ganache第一个账户的私钥
            web3.eth.accounts.wallet.add(
              "0x3fee78f356fb8fe505b4e6e77aed6350a61c47878e01267203e6ead9cb5fa147"
            );

            web3.eth.defaultAccount = "0x2f73662dE3527F97a582836BebF96ef22EC029Fa";
        //   }

         this.getlength();
            this.Read();
        });

      }

    getlength = async () => {
        // alert(web3.eth.defaultAccount);
        // alert("hello2");
        // alert("123");
        if( web3.eth.defaultAccount != 0){
            const payload = web3.eth.abi.encodeFunctionCall(
                {
                  name: "Length",
                  type: "function",
                  inputs: [{
                      type: 'address',
                      name: '_address'
                  }]
                },
                [web3.eth.defaultAccount]
            );

            const res = await web3.eth.call({
                to: CONTRACT,
                data: payload
            });

            this.setState({
                length: web3.eth.abi.decodeParameter("uint256", res)
            });  
            // alert(this.state.length);
        }

    };

    WriteNote = async () => {
        const funcSig = web3.eth.abi.encodeFunctionSignature("Write(address,string)");
        const param = web3.eth.abi.encodeParameters(['address','string'],[web3.eth.defaultAccount,'HiHiHi!']);
        try {
            await web3.eth.sendTransaction({
              to: CONTRACT, // contract address
              data: funcSig + param.slice(2),
              gas: 2000000
            });
            // alert("write success");
          } catch (e) {
            // alert(e.message);
          } finally {
            this.getlength(); 
          }


    };

    componentDidMount() {
        this.screenChange();
    }
    screenChange() {
        window.addEventListener('resize', this.resize);
    }
    constructor(props) {
        this.resize.bind(this);
    }
    componentWillUnmount() {       
        window.removeEventListener('resize',this.resize);
    }

    Read = async () => {
        this.setState({
            loading: true
        }); 
        var i=0;
        if( web3.eth.defaultAccount != 0){
            for(i=this.state.idx;i<this.state.length;i++){
                const payload = web3.eth.abi.encodeFunctionCall(
                    {
                      name: "Read",
                      type: "function",
                      inputs: [{
                          type: 'address',
                          name: '_address',
                      },{
                          type: 'uint256',
                          name: 'index',
                      }]
                    },
                    [web3.eth.defaultAccount,i]
                );
                const res = await web3.eth.call({
                    to: CONTRACT,
                    data: payload
                });
                this.setState({
                    content: web3.eth.abi.decodeParameter("string", res),
                    idx:i+1
                });   
                notes.push({'content': this.state.content,'idx': i.toString()})
                this.changeShowState2true();
            }               
        }
        this.setState({
            loading: false
        });    
    };

    toggle = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    }

    enterLoading = () => {
        this.setState({ loading: true });
    };

    enterIconLoading = () => {
        this.setState({ iconLoading: true });
    };

    enterIconLoading = () => {
        this.setState({ nno: true });
    };

    setshowState = (content) => {
        this.setState({mynotes: content});
    };

    setAccount = (account) => {
        this.setState({account: account});
        // web3.eth.defaultAccount = this.state.account;
    };

    changeShowState2true() {
        this.setState((state) => {
          // 重要：在更新的时候读取 `state`，而不是 `this.state`。
          return {showstate: true}
        });
      }

    click_1 = (data) => {
        if(this.state.loading == true) return;
        // window.location.reload(true); 
        // document.getElementById("shownotes").innerHTML=this.state.mynotes;
        this.DisplayAndHiddenBtn("shownotes","d");
        this.DisplayAndHiddenBtn("upload_div","h");
        this.DisplayAndHiddenBtn("search_input","h");
        // alert(this.state.mynotes);
        // alert(this.state.showstate);
        // document.getElementById("_button").innerHTML="";
        // document.getElementById("_input").innerHTML="";
        // notes.push({ username: 'Jerry', age: 21, id: '1', gender: 'male' })
        // alert(`${data}`);
    };
    click_2 = (data) => {
        if(this.state.loading == true) return;
        // window.location.reload(true); 
        this.setshowState(data);
        // document.getElementById("_button").innerHTML="";
        // document.getElementById("shownotes").innerHTML="";
        // document.getElementById("_button").innerHTML="\
        // \<button type =\"button\" class=\"ant-btn ant-btn-primary\" id = \"_button\" onClick=this.getlength(); >\
        // \<span>Click to Upload!</span>\
        // \</button>";
        // shownotes
        this.DisplayAndHiddenBtn("shownotes","h");
        this.DisplayAndHiddenBtn("upload_div","d");
        this.DisplayAndHiddenBtn("search_input","h");
        window.onresize = this.autodivheight();
        // document.getElementById("_input").innerHTML="\
        // \<div id=\"_input\" separator=\"/\">\
        // \    <span class=\"ant-input-affix-wrapper\">\
        // \        <input placeholder=\"fuck2\" type=\"text\" class=\"ant-input\" value></input>\
        // \        <span class=\"ant-input-suffix\"></span>\
        // \   </span>\
        // \   <br></br>\
        // \   <span class=\"ant-input-affix-wrapper ant-input-affix-wrapper-textarea-with-clear-btn\">\
        // \       <textarea rows=\"25\" cols=\"20\" placeholder=\"fuck2\" class=\"ant-input\"></textarea>\
        // \   </span>\
        // \</div>\
        // ";
    };
    click_3 = (data) => {
        if(this.state.loading == true) return;
        // window.location.reload(true); 
        this.setshowState(data);
        // document.getElementById("_button").innerHTML="";
        this.DisplayAndHiddenBtn("shownotes","h");
        this.DisplayAndHiddenBtn("search_input","d");
        this.DisplayAndHiddenBtn("upload_div","h");
        // document.getElementById("shownotes").innerHTML="";
        // document.getElementById("_button").innerHTML="\
        // \<div align=\"center\" >\
        // \<button type =\"button\" class=\"ant-btn ant-btn-primary\" id = \"_button\" onClick=alert(\"1234\") >\
        // \<span>Click to Find!</span>\
        // \</button>\
        // \</div>\
        // ";
        // document.getElementById("_input").innerHTML="\
        // \<div id=\"_input\" separator=\"/\">\
        // \    <span class=\"ant-input-affix-wrapper\">\
        // \        <input placeholder=\"Input the Address\" type=\"text\" class=\"ant-input\" value></input>\
        // \        <span class=\"ant-input-suffix\"></span>\
        // \   </span>\
        // \</div>\
        // ";
    };

    DisplayAndHiddenBtn = (btnId, type)=> {
        var currentBtn = document.getElementById(btnId);
        if (type == "d") {
                currentBtn.style.display = "block"; //style中的display属性
            }
        else if (type == "h") {
                currentBtn.style.display = "none";
            }
        }
    // clicking My notes triggers
    render() {
        const { name } = this.state;
        return (
            <div id = "test">
            <Layout>
                <Sider
                    trigger={null}
                    collapsible
                    collapsed={this.state.collapsed}
                >

                    <div className="logo" >
                        <span className="nav-text"  ></span>
                    </div>
                    <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                        <Menu.Item key="1" onClick={() => {this.click_1("hello")}}>
                            <Icon type="user" />
                            <span className="nav-text" >My Notes</span>
                        </Menu.Item>
                        <Menu.Item key="2" onClick={() => {this.click_2(document.getElementById("shownotes").innerHTML)}}>
                            <Icon type="upload" />
                            <span className="nav-text" >Upload new notes</span>
                        </Menu.Item>
                        <Menu.Item key="3" onClick={() => {this.click_3(document.getElementById("shownotes").innerHTML)}}>
                            <Icon type="right-circle" />
                            <span className="nav-text"  >Find notes</span>
                        </Menu.Item>

                    </Menu>
                </Sider>
                <Layout>
                    <Header style={{ background: '#000', padding: 0 }}>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>
                            <Icon
                                className="trigger"
                                type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
                                onClick={this.toggle}
                                style={{ cursor: 'pointer' }}
                            />
                        </span>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>Dapp Note</span>
                        <span style={{ color: '#fff', float: 'right', paddingRight: '1%' }}>
                            <img src={logo} className="App-logo" alt="logo" />
                        </span>
                        {/* <div onClick={this.click_1(name)}> test react </div> */}
                    </Header>

                    <Content style={{ margin: '0 160px' }} >
                        <Breadcrumb style={{ margin: '20px 0' }}>
                            <Breadcrumb.Item>xxxx</Breadcrumb.Item>
                            <div id="shownotes" style={{display:'block'}}>
                            <Collapse accordion >
                                    {notes.map((note) => {
                                    return (
                                        <Panel header={note.idx} key={note.idx}>
                                            <p>{note.content}</p>
                                        </Panel>
                                    )
                                    })}
    {/* 
                                    <Panel header="This is panel header 1" key="1">
                                    <p>{text}</p>
                                    </Panel>
                                    <Panel header="This is panel header 2" key="2">
                                    <p>{text}</p>
                                    </Panel>
                                    <Panel header="This is panel header 3" key="3">
                                    <p>{text}</p>
                                    </Panel> */}
                                </Collapse>
                            </div>

                            {/* <div>
                                {users.map((user) => {
                                return (
                                    <div>
                                    <div>姓名：{user.username}</div>
                                    <div>年龄：{user.age}</div>
                                    <div>性别：{user.gender}</div>
                                    <hr />
                                    </div>
                                )
                                })}
                            </div> */}

                        </Breadcrumb>
                        <div id = "upload_div" style={{display:'none'}}>
                                <Input  placeholder="input with clear icon" allowClear onChange={onChange} />
