import { Layout, Menu, Breadcrumb, Icon } from 'antd';
import React, { Component } from 'react';
import 'antd/dist/antd.css';
import logo from '../logo.svg';
import './Navi.css';
import { Button } from 'antd';
import { Input } from 'antd';
import { Collapse } from 'antd';
import Web3 from "web3";



let web3 = window.web3;
const { Header, Content, Footer, Sider } = Layout;
const { TextArea,Search } = Input;
const { Panel } = Collapse;
const { SubMenu } = Menu;
const text = `
  A dog is a type of domesticated animal.
  Known for its loyalty and faithfulness,
  it can be found as a welcome guest in many households across the world.
`;
const onChange = e => {
    console.log(e);
};
const CONTRACT = "0xd4b7ed93256Df5E6FeA90977C835850Ac5F17873";


class SiderDemo extends Component {

    state = {
        collapsed: false,
        mode: 'inline',
        loading: false,
        iconLoading: false,
        showstate: false,
        // length: 0,
        // ohter_length:0,
        // idx:0,
        // other_idx:0,
        content: "",
        mynotes:"" ,
        loading:false,
        account:"",
        componnetArray:[1],
        deskDivWidth:800,
        deskHeight:document.body.clientHeight
    };

    notes = {
        length:0,
        idx:0,
        items:[]
        // {content : '' , idx: "123"}
    }

    other_notes = {
        length:0,
        idx:0,
        items:[]
        // {content : '' , idx: "123"}
    }

    edit_idx = -1;

    componentWillMount() {
        window.addEventListener("load", async () => {
        //   if (window.ethereum) {
        //     // console.log("init metamask", window.ethereum);
        //     web3 = new Web3(window.ethereum);
        //     await window.ethereum.enable();
        //     web3.eth.getAccounts(function(err, accounts) {
        //       web3.eth.defaultAccount = accounts[0];
        //       alert(web3.eth.defaultAccount);
        //     });
        //   } else {
            console.log("inject sdk");
            web3 = new Web3(
              new Web3.providers.HttpProvider("http://localhost:7545")
            );
            // 添加Ganache第一个账户的私钥
            web3.eth.accounts.wallet.add(
              "0x9d3908699ff0fff75d409fc5bac0ea322cc6287c47299959ee291aef6cfe67a4"
            );

            web3.eth.defaultAccount = "0x819De97b7B694941D4F82a9C9B4af544109407F9";
        //   }

        await this.getlength(web3.eth.defaultAccount,1);
        this.Read(web3.eth.defaultAccount,1);
        });

      }

    getlength = async (account,my) => {
        // alert(web3.eth.defaultAccount);
        // alert("hello2");
        // alert("123");
        if( account != 0){
            const payload = web3.eth.abi.encodeFunctionCall(
                {
                  name: "Length",
                  type: "function",
                  inputs: [{
                      type: 'address',
                      name: '_address'
                  }]
                },
                [account]
            );

            const res = await web3.eth.call({
                to: CONTRACT,
                data: payload
            });
            // alert(web3.eth.abi.decodeParameter("uint256", res));
            if(my == 1){
                this.notes["length"] = web3.eth.abi.decodeParameter("uint256", res);
                // alert(this.notes["length"]);
            }
            else{
                this.other_notes["length"] = web3.eth.abi.decodeParameter("uint256", res);
            }
            // this.setState({
            //     length: web3.eth.abi.decodeParameter("uint256", res)
            // });  
            // alert(this.state.length);
        }

    };

    WriteNote = async (content) => {
        // alert(content);
        const funcSig = web3.eth.abi.encodeFunctionSignature("Write(address,string)");
        const param = web3.eth.abi.encodeParameters(['address','string'],[web3.eth.defaultAccount,content]);
        try {
            await web3.eth.sendTransaction({
              to: CONTRACT, // contract address
              data: funcSig + param.slice(2),
              gas: 2000000
            });
            alert("Write done.");
            // alert("write success");
          } catch (e) {
            alert(e.message);
          } finally {
            this.getlength(web3.eth.defaultAccount,this.notes); 
          }  
    };

    ModifyNote = async (content) => {
        alert(this.edit_idx);
        const funcSig = web3.eth.abi.encodeFunctionSignature("Modify(address,uint256,string)");
        const param = web3.eth.abi.encodeParameters(['address','uint256','string'],[web3.eth.defaultAccount,this.edit_idx,content]);
        try {
            await web3.eth.sendTransaction({
              to: CONTRACT, // contract address
              data: funcSig + param.slice(2),
              gas: 2000000
            });
            alert("Modify done.");
            // alert("write success");
          } catch (e) {
            alert(e.message);
          } finally {
            this.getlength(web3.eth.defaultAccount,this.notes); 
          }  
    }

    Read = async (account,my) => {
        // alert(this.notes["length"]);
        this.setState({
            loading: true
        }); 
        var i=0;
        var length = 0;
        if(my == 1){
            i = this.notes["idx"]; 
            length = this.notes["length"];
        }
        else{
            i = this.other_notes["idx"]; 
            length = this.other_notes["length"];
        }
        if( web3.eth.defaultAccount != 0){
            for(;i<length;i++){
                const payload = web3.eth.abi.encodeFunctionCall(
                    {
                      name: "Read",
                      type: "function",
                      inputs: [{
                          type: 'address',
                          name: '_address',
                      },{
                          type: 'uint256',
                          name: 'index',
                      }]
                    },
                    [account,i]
                );
                const res = await web3.eth.call({
                    to: CONTRACT,
                    data: payload
                });
                this.setState({
                    content: web3.eth.abi.decodeParameter("string", res),
                    // idx:i+1
                });  
                if(my == 1){
                    this.notes["idx"] = i+1; 
                    this.notes["items"].push({'content': this.state.content.substr(this.state.content.indexOf("\n\n")+2).replace(/\n/g,"\n\n"),'idx': i.toString(), 'title': this.state.content.substr(0,this.state.content.indexOf("\n\n"))})
                }
                else{
                    this.other_notes["idx"] = i+1; 
                    this.other_notes["items"].push({'content': this.state.content.substr(this.state.content.indexOf("\n\n")+2).replace(/\n/g,"\n\n"),'idx': i.toString(), 'title': this.state.content.substr(0,this.state.content.indexOf("\n\n"))})
                }
                this.changeShowState2true();
            }            

        }
        // alert(this.notes["items"][2]["content"]);
        this.setState({
            loading: false
        });    
    };

    toggle = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    }

    enterLoading = () => {
        this.setState({ loading: true });
    };

    enterIconLoading = () => {
        this.setState({ iconLoading: true });
    };

    enterIconLoading = () => {
        this.setState({ nno: true });
    };

    setshowState = (content) => {
        this.setState({mynotes: content});
    };

    setAccount = (account) => {
        this.setState({account: account});
        // web3.eth.defaultAccount = this.state.account;
    };

    changeShowState2true() {
        this.setState((state) => {
          // 重要：在更新的时候读取 `state`，而不是 `this.state`。
          return {showstate: true}
        });
      }

    echo = (data) => {
        alert(data);
    }

    click_1 = (data) => {
        if(this.state.loading == true) return;
        // window.location.reload(true); 
        // document.getElementById("shownotes").innerHTML=this.state.mynotes;
        this.displayAndHiddenBtn("shownotes","d");
        this.displayAndHiddenBtn("upload_div","h");
        this.displayAndHiddenBtn("search_input","h");
        this.displayAndHiddenBtn("show_other_notes","h");
        this.displayAndHiddenBtn("edit_div", "h");
        // alert(this.state.mynotes);
        // alert(this.state.showstate);
        // document.getElementById("_button").innerHTML="";
        // document.getElementById("_input").innerHTML="";
        // notes.push({ username: 'Jerry', age: 21, id: '1', gender: 'male' })
        // alert(`${data}`);
    };
    click_2 = (data) => {
        if(this.state.loading == true) return;
        // window.location.reload(true); 
        this.setshowState(data);
        this.displayAndHiddenBtn("shownotes","h");
        this.displayAndHiddenBtn("upload_div","d");
        this.displayAndHiddenBtn("search_input","h");
        this.displayAndHiddenBtn("show_other_notes","h");
        this.displayAndHiddenBtn("edit_div", "h");
    };
    click_3 = (data) => {
        if(this.state.loading == true) return;
        // window.location.reload(true); 
        this.setshowState(data);
        // document.getElementById("_button").innerHTML="";
        this.displayAndHiddenBtn("shownotes","h");
        this.displayAndHiddenBtn("upload_div","h");
        this.displayAndHiddenBtn("search_input","d");
        this.displayAndHiddenBtn("show_other_notes","h");
        this.displayAndHiddenBtn("edit_div", "h");
    };

    click_4 = async (account) => {
        // alert(account);
        try {
            // account = "0x3414d7268DDD6EC483f6EdB78F1369F6732C5D45";
            await this.getlength(account,0);
            // this.setState({idx : 0});
            this.Read(account,0);
            this.displayAndHiddenBtn("shownotes","h");
            this.displayAndHiddenBtn("upload_div","h");
            this.displayAndHiddenBtn("search_input","d");
            this.displayAndHiddenBtn("show_other_notes","d");
            this.displayAndHiddenBtn("edit_div", "h");
          } catch (e) {
            alert(e.message);
          } finally {
            this.setState({
              loading: false
            });
          }
    }
    click_5 = async (title,content) => {
        var tmp;
        tmp = title+"\n\n"+content;
        // alert(tmp);
        await this.WriteNote(tmp);
        // alert("Done");
        window.location.reload(true); 
    };

    click_6 = (title) => {
        alert("inside");
        var i=0;
        var content, title;
        var find = 0;
        for(i=0;i<this.notes["length"];++i){
            if(this.notes["items"][i]["title"] === title){
                content = this.notes["items"][i]["content"];
                title = this.notes["items"][i]["title"];
                document.getElementById("edit_content").value = content;
                document.getElementById("edit_title").value = title;
                alert(content + "\n\n" + title);
                this.edit_idx = i;
                find = 1;
                break;
            }
        }

        if(find == 0)
        {
            alert("Not found!");
        }
    };

    click_7 = (data) => {
        if(this.state.loading == true) return;
        // window.location.reload(true); 
        this.setshowState(data);
        // document.getElementById("_button").innerHTML="";
        this.displayAndHiddenBtn("shownotes","h");
        this.displayAndHiddenBtn("upload_div","h");
        this.displayAndHiddenBtn("search_input","h");
        this.displayAndHiddenBtn("show_other_notes","h");
        this.displayAndHiddenBtn("edit_div", "d");
    }

    click_8 = async (title, content) => {
        // check
        if(this.edit_idx == -1)
        {
            alert("Modify error, you must get the note you want to modify first!");
            return;
        }
        var tmp;
        tmp = title+"\n\n"+content;
        // alert(tmp);
        await this.ModifyNote(tmp);
        this.edit_idx = -1;
        // alert("Done");
        window.location.reload(true); 
    }


    displayAndHiddenBtn = (btnId, type)=> {
        var currentBtn = document.getElementById(btnId);
        if (type == "d") {
                currentBtn.style.display = "block"; //style中的display属性
            }
        else if (type == "h") {
                currentBtn.style.display = "none";
            }
        }
    // clicking My notes triggers
    render() {
        const { name } = this.state;
        return (
            // <div id = "fuck" >
            <Layout>
                <Sider
                    trigger={null}
                    collapsible
                    collapsed={this.state.collapsed}
                >

                    <div className="logo" >
                        <span className="nav-text"  ></span>
                    </div>
                    <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                        <Menu.Item key="1" onClick={() => {this.click_1("hello")}}>
                            <Icon type="user" />
                            <span className="nav-text" >My Notes</span>
                        </Menu.Item>
                        <SubMenu
                            key="sub1"
                            title={
                            <span>
                                <Icon type="mail" />
                                <span>New / Edit</span>
                            </span>
                            }
                        >
                        <Menu.Item key="2" onClick={() => {this.click_2(document.getElementById("shownotes").innerHTML)}}>
                            <Icon type="upload" />
                            <span className="nav-text" >New note</span>
                        </Menu.Item>
                        <Menu.Item key="3" onClick={() => {this.click_7(document.getElementById("shownotes").innerHTML)}}>
                            <Icon type="upload" />
                            <span className="nav-text" >Edit note</span>
                        </Menu.Item>
                        </SubMenu>
                        <Menu.Item key="4" onClick={() => {this.click_3(document.getElementById("shownotes").innerHTML)}}>
                            <Icon type="right-circle" />
                            <span className="nav-text"  >Find notes</span>
                        </Menu.Item>

                    </Menu>
                </Sider>
                <Layout>
                    <Header style={{ background: '#000', padding: 0 }}>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>
                            <Icon
                                className="trigger"
                                type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
                                onClick={this.toggle}
                                style={{ cursor: 'pointer' }}
                            />
                        </span>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>Dapp Note</span>
                        <span style={{ color: '#fff', float: 'right', paddingRight: '1%' }}>
                            <img src={logo} className="App-logo" alt="logo" />
                        </span>
                        {/* <div onClick={this.click_1(name)}> test react </div> */}
                    </Header>


                    <Content style={{ margin: '0 160px' }} >
                    <div style={{ padding: 24, background: '', minHeight: document.body.clientHeight }}>
                        <Breadcrumb style={{ margin: '20px 0' }}>
                            <Breadcrumb.Item>Notes</Breadcrumb.Item>
                            <br />
                            <div id="shownotes" style={{display:'block'}}>
                            <Collapse accordion >
                                    {this.notes["items"].map((note) => {
                                    return (
                                        <Panel header={note.title} key={note.idx}>
                                            <p>{note.content}</p>
                                        </Panel>
                                    )
                                    })}
                                </Collapse>
                            </div>
                        </Breadcrumb>           
                        <div id = "upload_div" style={{display:'none'}}>
                                <Input  id = "input_title" placeholder="input with title " allowClear onChange={onChange} required/>
                                <br />
                                <br />
                                <TextArea id = "input_content" rows="25" cols="20" placeholder="textarea with clear icon" allowClear onChange={onChange} />
                                <Button  type="primary"  onClick={() => {this.click_5(document.getElementById("input_title").value,document.getElementById("input_content").value)}} >
                                    Click to Upload!
                                </Button>
                        </div>
                        <div id = "edit_div" style={{display:'none'}}>
                            <div >
                                    <Search
                                        id = "search_accord_title"
                                        placeholder="input title"
                                        enterButton="Search"
                                        size="large"
                                        onSearch={value => this.click_6(value)}
                                    />
                            </div>
                            <br />
                            <br />
                            <Input  id = "edit_title" placeholder="Your original title will be put here " allowClear onChange={onChange}/>
                            <br />
                            <br />
                            <TextArea id = "edit_content" rows="40" cols="20" placeholder="Your original content will be put here" allowClear onChange={onChange} />
                            <Button  type="primary" loading={this.state.loading} onClick={() => {this.click_8(document.getElementById("edit_title").value,document.getElementById("edit_content").value)}}>
                                Click to Edit!
                            </Button>
                        </div>
                        <div id = "search_input" style={{display:'none'}}>
                                <Search
                                    id = "search_content"
                                    placeholder="input search text"
                                    enterButton="Search"
                                    size="large"
                                    onSearch={value => this.click_4(value)}
                                />
                        </div>
                        <Breadcrumb style={{ margin: '20px 0' }} id="show_other_notes" style={{display:'none'}}>
                            <Breadcrumb.Item>xxxx</Breadcrumb.Item>
                            <div >
