import { Layout, Menu, Breadcrumb, Icon } from 'antd';
import React, { Component } from 'react';
import 'antd/dist/antd.css';
import logo from '../logo.svg';
import './Navi.css';
import { Mentions, Form, Button } from 'antd'
const { Header, Content, Footer, Sider } = Layout;
const { Option, getMentions } = Mentions;

class SiderDemo extends Component {
    state = {
        collapsed: false,
        mode: 'inline',
        loading: false,
        iconLoading: false,
    };

    toggle = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    }

    enterLoading = () => {
        this.setState({ loading: true });
    };

    enterIconLoading = () => {
        this.setState({ iconLoading: true });
    };

    handleReset = e => {
        e.preventDefault();
        this.props.form.resetFields();
    };

    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((errors, values) => {
            if (errors) {
                console.log('Errors in the form!!!');
                return;
            }
            console.log('Submit!!!');
            console.log(values);
        });
    };

    checkMention = (rule, value, callback) => {
        const mentions = getMentions(value);
        if (mentions.length < 2) {
            callback(new Error('More than one must be selected!'));
        } else {
            callback();
        }
    };
    render() {
        const {
            form: { getFieldDecorator },
        } = this.props;
        return (
            <Layout>
                <Sider
                    trigger={null}
                    collapsible
                    collapsed={this.state.collapsed}
                >
                    <div className="logo" />
                    <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                        <Menu.Item key="1">
                            <Icon type="user" />
                            <span className="nav-text">My Notes</span>
                        </Menu.Item>
                        <Menu.Item key="2">
                            <Icon type="upload" />
                            <span className="nav-text">Upload new notes</span>
                        </Menu.Item>
                        <Menu.Item key="3">
                            <Icon type="right-circle" />
                            <span className="nav-text">Find notes</span>
                        </Menu.Item>
                    </Menu>
                </Sider>
                <Layout>
                    <Header style={{ background: '#000', padding: 0 }}>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>
                            <Icon
                                className="trigger"
                                type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
                                onClick={this.toggle}
                                style={{ cursor: 'pointer' }}
                            />
                        </span>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>Dapp Note</span>
                        <span style={{ color: '#fff', float: 'right', paddingRight: '1%' }}>
                            <img src={logo} className="App-logo" alt="logo" />
                        </span>
                    </Header>
                    <Content style={{ margin: '0 16px' }}>
                        <Breadcrumb style={{ margin: '12px 0' }}>
                            <Breadcrumb.Item>Here is your note content</Breadcrumb.Item>
                            <Form layout="horizontal">
                                <Form.Item label="Top coders" labelCol={{ span: 6 }} wrapperCol={{ span: 16 }}>
                                    {getFieldDecorator('coders', {
                                        rules: [{ validator: this.checkMention }],
                                    })(
                                        <Mentions rows="1">
                                            <Option value="afc163">afc163</Option>
                                            <Option value="zombieJ">zombieJ</Option>
                                            <Option value="yesmeck">yesmeck</Option>
                                        </Mentions>,
                                    )}
                                </Form.Item>
                                <Form.Item label="Bio" labelCol={{ span: 6 }} wrapperCol={{ span: 16 }}>
                                    {getFieldDecorator('bio', {
                                        rules: [{ required: true }],
                                    })(
                                        <Mentions rows="3" placeholder="You can use @ to ref user here">
                                            <Option value="afc163">afc163</Option>
                                            <Option value="zombieJ">zombieJ</Option>
                                            <Option value="yesmeck">yesmeck</Option>
                                        </Mentions>,
                                    )}
                                </Form.Item>
                                <Form.Item wrapperCol={{ span: 14, offset: 6 }}>
                                    <Button type="primary" onClick={this.handleSubmit}>
                                        Submit
          </Button>
                                    &nbsp;&nbsp;&nbsp;
          <Button onClick={this.handleReset}>Reset</Button>
                                </Form.Item>
                            </Form>

                        </Breadcrumb>
                        <div style={{ padding: 24, background: '#fff', minHeight: 780 }}>
                            sadasdas
                        </div>
                    </Content>
                    <Footer style={{ textAlign: 'center' }}>
                        {/* Ant Design Â©2016 Created by Ant UED */}
                        Designed By Martin
                    </Footer>
                </Layout>
            </Layout>
        );
    }
}

export default SiderDemo;
