import { Layout, Menu, Breadcrumb, Icon } from 'antd';
import React, { Component } from 'react';
import 'antd/dist/antd.css';
import logo from '../logo.svg';
import './Navi.css';
import { Button } from 'antd';
import { Input } from 'antd';
const { Header, Content, Footer, Sider } = Layout;
const { TextArea } = Input;
const onChange = e => {
    console.log(e);
};



class SiderDemo extends Component {
    state = {
        collapsed: false,
        mode: 'inline',
        loading: false,
        iconLoading: false,
        name: 'yt'
    };

    toggle = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    }

    enterLoading = () => {
        this.setState({ loading: true });
    };

    enterIconLoading = () => {
        this.setState({ iconLoading: true });
    };

    click2 = () => {
        alert("asdasd");
    };

    handleClick = () => {
        document.getElementById("_button").innerHTML="<button type =\"button\" class=\"ant-btn ant-btn-primary\" id = \"_button\" onClick=\"click2()\"><span>Click to fuck!</span></button>";
    };

    render() {
        const { name } = this.state;
        return (
            <Layout>
                <Sider
                    trigger={null}
                    collapsible
                    collapsed={this.state.collapsed}
                >

                    <div className="logo" >
                        <span className="nav-text"  ></span>
                    </div>
                    <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                        <Menu.Item key="1">
                            <Icon type="user" />
                            <span className="nav-text" onClick={() => {this.handleClick()}}>My Notes</span>
                        </Menu.Item>
                        <Menu.Item key="2">
                            <Icon type="upload" />
                            <span className="nav-text" >Upload new notes</span>
                        </Menu.Item>
                        <Menu.Item key="3">
                            <Icon type="right-circle" />
                            <span className="nav-text">Find notes</span>
                        </Menu.Item>
                    </Menu>
                </Sider>
                <Layout>
                    <Header style={{ background: '#000', padding: 0 }}>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>
                            <Icon
                                className="trigger"
                                type={this.state.collapsed ? 'menu-unfold' : 'menu-fold'}
                                onClick={this.toggle}
                                style={{ cursor: 'pointer' }}
                            />
                        </span>
                        <span style={{ color: '#fff', paddingLeft: '2%', fontSize: '1.4em' }}>Dapp Note</span>
                        <span style={{ color: '#fff', float: 'right', paddingRight: '1%' }}>
                            <img src={logo} className="App-logo" alt="logo" />
                        </span>
                        {/* <div onClick={this.handleClick(name)}> test react </div> */}
                    </Header>

                    <Content style={{ margin: '0 160px' }} >
                        <Breadcrumb style={{ margin: '20px 0' }}>
                            <Breadcrumb.Item>Here is</Breadcrumb.Item>
                            <div>
                                <Input placeholder="input with clear icon" allowClear onChange={onChange} />
                                <br />
                                <br />
                                <TextArea rows="25" cols="20" placeholder="textarea with clear icon" allowClear onChange={onChange} />
                            </div>
                            {/* <Breadcrumb.Item>Bill</Breadcrumb.Item> */}
                        </Breadcrumb>
                        <div style={{ padding: 24, background: '#f0f2f5', minHeight: 780 }} id = "_button">
                            {/* sadasdas */}
                            {/* <div onClick={() => {this.handleClick(name)}}> test react </div> */}
                            <Button type="primary" loading={this.state.loading} onClick={this.enterLoading}>
                                Click to Upload!
                            </Button>
                        </div>
                    </Content>
                    <Footer style={{ textAlign: 'center' }}>
                        {/* Ant Design Â©2016 Created by Ant UED */}
                        Designed By Martin
                    </Footer>
                </Layout>
            </Layout>
        );
    }
}

export default SiderDemo;
